mapper.select:
SELECT inquiryno, memberno, inquiryTitle, inquiryReason, contentsno
FROM member
WHERE memberno = #{memberno} AND inquiryno = #{inquiryno}

SELECT memberno, id, passwd, name, tel, rdate, rankno
FROM member
WHERE memberno = #{memberno}

mapper.update:
UPDATE member 
SET id=#{id}, name=#{name}, tel=#{tel}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE memberno=#{memberno} AND passwd=#{passwd}

mapper.update:
UPDATE member
SET passwd=#{passwd}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}
