mapper.update:
UPDATE contents
SET title = #{title},
    content = #{content}, word = #{word}, file1 = #{file1}, file1saved= #{file1saved}
WHERE contentsno = #{contentsno}

UPDATE contents
SET viewcnt = viewcnt + 1
WHERE contentsno = #{contentsno}

mapper.select:
SELECT contentsno, memberno, boardno, title, content, rdate, file1, file1saved, size1, word, viewcnt, r FROM ( SELECT contentsno, memberno, boardno, title, content, rdate, file1, file1saved, size1, word, viewcnt, rownum as r FROM ( SELECT contentsno, memberno, boardno, title, content, rdate, file1, file1saved, size1, word, viewcnt FROM contents
WHERE boardno=#{boardno}
WHERE boardno=#{boardno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
WHERE (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY contentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*)
FROM contents
WHERE boardno = #{boardno}
