mapper.update:
UPDATE guin_c
SET viewcnt = viewcnt + 1
WHERE guin_cno = #{guin_cno}

UPDATE guin_c
SET map = #{map}
WHERE guin_cno = #{guin_cno}

mapper.select:
SELECT guin_cno, memberno, jobcateno, name, brand, title, content, rdate, address, map, wage, day, period, tel, email, file1, file1saved, thumb1, size1, word, viewcnt, r FROM ( SELECT guin_cno, memberno, jobcateno, name, brand, title, content, rdate, address, map, wage, day, period, tel, email, file1, file1saved, thumb1, size1, word, viewcnt, rownum as r FROM ( SELECT guin_cno, memberno, jobcateno, name, brand, title, content, rdate, address, map, wage, day, period, tel, email, file1, file1saved, thumb1, size1, word, viewcnt FROM guin_c
WHERE jobcateno=#{jobcateno}
WHERE jobcateno=#{jobcateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
WHERE (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY guin_cno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*)
FROM guin_c
WHERE jobcateno = #{jobcateno}
