mapper.update:
UPDATE resume 
SET  title = #{title}, file1 = #{file1}, file1saved = #{file1saved}, thumb1=#{thumb1}, size1=#{size1}, 
     intro = #{intro}, tel = {tel}, address = {address}, career = #{career}, 
     skills = #{skills}, langskill = #{langskill}, wantjob = #{wantjob}
WHERE resumeno = #{resumeno}
